const baseUrl = "https://wuzzuf.net/search/jobs/?a=hpb&filters%5Bcountry%5D%5B0%5D=Egypt&q=software%20engineer&a=hpb";
const pagesToScrape = 3; // Change this number to scrape more pages
const tasks = [];

for (let i = 0; i < pagesToScrape; i++) {
  tasks.push({
    json: {
      url: `${baseUrl}&start=${i}`
    }
  });
}

return tasks;

