parameters.scriptCode:
// 1. Get the link from the current item
const jobLink = $input.item.json.Link;

if (!jobLink || jobLink === "No Link") {
  return [{ description: "No link provided" }];
}

// 2. Go to the page
// Note the '$' before page
await $page.goto(jobLink, { waitUntil: 'domcontentloaded', timeout: 30000 });

// 3. Try to click "See more" if it exists
try {
  const seeMoreSelector = 'button.show-more-less-html__button';
  // Wait up to 2 seconds for the button to appear
  await $page.waitForSelector(seeMoreSelector, { timeout: 2000 });
  await $page.click(seeMoreSelector);

  // Wait a tiny bit for the text to expand
  await new Promise(r => setTimeout(r, 500));
} catch (e) {
  // If button isn't found, just ignore and continue
}

// 4. Scrape the content
const description = await $page.evaluate(() => {
  // This selector targets the main description box on LinkedIn public pages
  const el = document.querySelector('.show-more-less-html__markup');
  return el ? el.innerText : "Description not found";
});

// 5. Return it as an ARRAY (Required by n8n)
return [{
  details: description
}];
